package com.estiam.amanciodossou.tabproject;import android.app.Application;/** * Created by amanciodossou on 02/02/2018. */public class Current extends Application{    private String id;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    private String title;    @Override    public String toString() {        return "Current{" +                "id='" + id + '\'' +                ", title='" + title + '\'' +                '}';    }}