package com.estiam.amanciodossou.tabproject;import android.content.Context;import android.graphics.Canvas;import android.graphics.Color;import android.graphics.Paint;import android.graphics.Path;import android.util.AttributeSet;import android.view.MotionEvent;import android.view.View;/** * Created by amanciodossou on 02/02/2018. */public class DrawerView extends View {    private Paint mPaint;    private Path mPath;    public DrawerView(Context context, AttributeSet attrs) {        super(context);        mPaint = new Paint();        mPaint.setColor(Color.RED);        mPaint.setStyle(Paint.Style.STROKE);        mPaint.setStrokeJoin(Paint.Join.ROUND);        mPaint.setStrokeCap(Paint.Cap.ROUND);        mPaint.setStrokeWidth(10);        mPath = new Path();    }    @Override    protected void onDraw(Canvas canvas) {        canvas.drawPath(mPath, mPaint);        super.onDraw(canvas);    }    @Override    public boolean onTouchEvent(MotionEvent event) {        switch (event.getAction()){            case MotionEvent.ACTION_DOWN:                mPath.moveTo(event.getX(), event.getY());                break;            case MotionEvent.ACTION_MOVE:                mPath.lineTo(event.getX(), event.getY());                invalidate();                break;            case MotionEvent.ACTION_UP:                break;        }        return true;    }}